import React, { useState } from 'react'

const App = () => {
  // tallenna napit omaan tilaansa
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [allClicks, setAll] = useState(0)
  const [pisteet, setPisteet] = useState(0)
  const [keskiarvo, laskeKeskiarvo] = useState(0)
  const [posit, laskePosit] = useState(0)

  const handleGoodClick = () => {
    setAll(allClicks +1)
    setGood(good + 1)
    setPisteet(pisteet +1)
    laskeKeskiarvo(pisteet/allClicks)
    laskePosit(good*1.0/allClicks*1.0)
  }
  const handleNeutralClick = () => {
    setAll(allClicks +1)
    setNeutral(neutral + 1)
    laskeKeskiarvo(pisteet/allClicks)
    laskePosit(good/allClicks)
  }
  const handleBadClick = () => {
    setAll(allClicks +1)
    setBad(bad + 1)
    setPisteet(pisteet -1)
    laskeKeskiarvo(pisteet/allClicks)
    laskePosit(good/allClicks)
  }

  return (
    <div>
      <h1> give feedback</h1>
       <div>
        <button onClick={handleGoodClick}>
          Good
        </button>
        <button onClick={handleNeutralClick}>
          Neutral
        </button>
        <button onClick={handleBadClick}>
          Bad
        </button>
      </div>
      <div>
        <h1>statistics</h1>
        <p>good {good}</p>
        <p>neutral {neutral}</p>
        <p>bad {bad}</p>
        <p>all {allClicks}</p>
        <p>average {keskiarvo}</p>
        <p>positive {posit}</p>
      </div>
    </div>
  )
}

export default App
