const Header = (props) => {
  return(
    <div>
      <h1>
       { props.course }
      </h1>
    </div>
  )
}
const Content = ({parts}) => {
  return(
    <div>
    <table>
      {parts.map(part => <Part key={part.id} part={part}/>)}
    </table>
    </div>
  )
}
const Total = ({parts}) => {
  const total = parts.reduce((summa, part) => summa + part.exercises, 0)
  return(
    <div>
      <h2>total of exercises {total}</h2>
    </div>
  )
}
const Part = ({part}) =>{
  return(
    <div>
      <tr>
          <td>{part.name}</td><td>{part.exercises}</td>
      </tr>
    </div>
  )
}
const Course = ({course}) => {
  return(
    <div>
      <Header course={course.name}></Header>
      <Content parts={course.parts}></Content>
      <Total parts={course.parts}></Total>
    </div>
  )
}

const App = () => {

  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    }, 
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]

  return (
    <div>
      {courses.map((course,id) => <Course key={id} course={course}/>)}
    </div>
  )
}
export default App;
