import React from 'react'

const Country = ({country}) => {
    return (
        <div>
            <p>{country.name}</p>
        </div>
    )
}
const Language = ({languages}) => {

    return (<div>
        {language.name}
    </div>

    )
}


const CountryInDetail = ({country}) => {
    const kielet = country.map((country)=>
    <Language key={country.name} languages={country.language}></Language>)
    return(
        <div>
            <h2>{country.name}</h2>
            <p>capital {country.capital}<br></br>
            population {country.population} </p>
            <h3>languages</h3>
            <li>

            </li>
        </div>
    )
}
const Countries = ({countries, filter}) => {
    console.log(filter)
    const filterilla = countries.filter(country =>
         country.name.toLowerCase().includes(filter.toLowerCase()));
         console.log(filterilla)
    const pituus = filterilla.map((country)=>
    <Country key={country.name} country={country}/>).length
    if (pituus > 10) {
        return(
            <div>
                <p>Too many matches, specify another filter</p>
            </div>
        )
    } if (pituus === 1){
        return(
            <div>
               {filterilla.map((country)=>
                <CountryInDetail key={country.name} country={country}/>)}
            </div>
        )
    }
     else {
        return (
            <div>
                {filterilla.map((country)=>
                <Country key={country.name} country={country}/>)}
            </div>
        )
    }

}
export default Countries
