import React, { useState, useEffect } from 'react'
import axios from 'axios'
import Filter from "./components/Filter.js"
import Person from "./components/Person.js"
import Persons from "./components/Persons.js"
import personService  from './services/persons.js'

const App = () => {
  const [persons, setPersons] = useState([])
  const [ newName, setNewName ] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newFilter, setNewFilter] = useState('')

  useEffect(() => {
    console.log('effect')
    personService
      .getAll()
        .then(initialPersons => {
        setPersons(initialPersons)
      })
  }, [])
  console.log('render', persons.length, 'persons')

  const handleNameChange = (event) => {
    console.log(event.target.value)
    setNewName(event.target.value)
  }
  const handleNumberChange = (event) => {
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }
  const handleFilterChange = (event) => {
    console.log(event.target.value)
    setNewFilter(event.target.value)
  }
  const handleRemove = person => {
    const response = window.confirm(`Poistetaatnko ${person.name}`)
    if (response){
      personService.remove(person)
    }
  }
  const addPerson = (event) => {
    event.preventDefault()
    console.log('button clicked', event.target)
    console.log(newName)
    console.log(newNumber)
    console.log(Object.entries(persons))
    if (persons.find(person => person.name === newName)) {
      alert(`${newName} is already added to phonebook`)
    } else {
      const personObject = {
        name: newName,
        number: newNumber,
        id: persons.length + 1,
      }
      personService
      .create(personObject)
        .then(returnedPerson => {
        setPersons(persons.concat(returnedPerson))
        setNewName('')
        setNewNumber('')
      })
    }
  }
  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={setNewFilter}>
        <div>
          <Filter onFilterChange={handleFilterChange} filter={newFilter}/>
        </div>
      </form>
      <h2>add a new</h2>
      <form onSubmit={addPerson}>
        <div>
        name: <input value={newName}onChange={handleNameChange} />
        </div>
        <div>
        number: <input value={newNumber}onChange={handleNumberChange} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
        <Persons persons={persons}filter={newFilter}handleRemove={handleRemove}/>
    </div>
  )

}

export default App
