import React, { useState, useEffect } from 'react'
import Filter from "./components/Filter.js"
import Persons from "./components/Persons.js"
import personService  from './services/persons.js'

const App = () => {
  const [persons, setPersons] = useState([])
  const [ newName, setNewName ] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newFilter, setNewFilter] = useState('')
  const [message, setMessage] = useState('something happened...')

  useEffect(() => {
    console.log('effect')
    personService
      .getAll()
        .then(initialPersons => {
        setPersons(initialPersons)
      })
  }, [])
  console.log('render', persons.length, 'persons', typeof(persons))

  const handleNameChange = (event) => {
    console.log(event.target.value)
    setNewName(event.target.value)
  }
  const handleNumberChange = (event) => {
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }
  const handleFilterChange = (event) => {
    console.log(event.target.value)
    setNewFilter(event.target.value)
  }
  const handleEdit = (person) => {
    console.log("edit", person)
    const newObject = personService.update(person)
    console.log(person)
    newObject
    .then(updatedPerson => {
      setPersons(persons.map(person => (person.id !== updatedPerson.id ? person : updatedPerson)))
      setNewName('')
      setNewNumber('')
      console.log("vaihdettu")
    })
  }
  const addPerson = (event) => {
    event.preventDefault()
    console.log('button clicked', event.target)
    console.log(newName)
    console.log(newNumber)
    const personToFind = persons.find(person => person.name === newName)
    if(personToFind){
      const response = window.confirm(`${newName} is already added to phonebook, replace the old number with a new one?`)
      if(response) {
        const person = persons.find(person => person.name === newName)
        return handleEdit({ ...person, number:newNumber})
      }
    } else {
      const personObject = {
        name: newName,
        number: newNumber,
      }
      console.log(personObject)
      personService
      .create(personObject)
        .then(returnedPerson => {
        setPersons(persons.concat(returnedPerson))
        setNewName('')
        setNewNumber('')
      }).catch(error => {
        console.log(error.response.data)
      })
      const successStyle={
        color: 'green',
        background: 'lightgrey',
        fontSize: 20

      } 
      setMessage(message= `Added ${newName}`)
    }
  }
  const handleRemove = (person) => {
    console.log('osuma')
    const response = window.confirm(`Poistetaanko ${person.name}`)
    if (response){
      personService.remove(person).finally(() => {
        const newPersons = persons.filter(p => p.id !== person.id);
        setPersons(newPersons)
      })
    }
  }


  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={setNewFilter}>
        <div>
          <Filter onFilterChange={handleFilterChange} filter={newFilter}/>
        </div>
      </form>
      <h2>add a new</h2>
      <form onSubmit={addPerson}>
        <div>
        name: <input value={newName}onChange={handleNameChange} />
        </div>
        <div>
        number: <input value={newNumber}onChange={handleNumberChange} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
        <div><Persons persons={persons}filter={newFilter}onRemove={handleRemove}/></div>
    </div>
  )

}

export default App
